import { useState, useEffect, useContext } from "react";
import axios from "axios";
import { Routes, Route, useNavigate } from "react-router";
import { AuthContext } from "../contexts/AuthContext";
import MovieSearch from "./MovieSearch";
import Movie from "./Movie";
import { Flex, Box, Stack, StackDivider } from "@chakra-ui/react";

const Home = () => {
  // const context = useContext(AuthContext) //escucha los nuevos seteos y actualiza el componente
  // console.log(context)
  const navigate = useNavigate();
  const [input, setInput] = useState([]);
  const [movies, setMovies] = useState([]);
  const userLog = useContext(AuthContext);
  const { toggleAuth, isLoggedIn, isAuthenticated } = userLog;

  useEffect(() => {
    //query to search movies for popularity --> i'll show them on the catalog on Home
    axios
      .get(
        `https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=7571866020be0fc96f4cbd335eaa5117&page=1`
      )
      .then((response) => setMovies(response.data.results));
  }, []);

  console.log(movies);

  return (
    <div>
      <h1 className="title">The Movie Database</h1>
      <Stack direction={{base: "column"}} divider={<StackDivider/>} spacing={2}>
        {movies.map((movie) => (
          <Movie key={movie.id} movieCard={movie} />
        ))}
      </Stack>
    </div>
  );
};

export default Home;
