import { useState, useEffect, useContext } from "react";
import axios from "axios";
import { Routes, Route, useNavigate } from 'react-router';
import MovieSearch from "./MovieSearch";
import { AuthContext } from "../contexts/AuthContext";


const Home = () => {

    // const context = useContext(AuthContext) //escucha los nuevos seteos y actualiza el componente
    // console.log(context)
    const navigate = useNavigate();
    const [input, setInput] = useState([]);
    const [movies, setMovies] = useState([]);

    useEffect( () => {
        axios.get("/api/me")
        .then()
    })

    const handleSubmit = (e) => {
        e.preventDefault()

        axios.get(`https://api.themoviedb.org/3/search/movie?api_key=7571866020be0fc96f4cbd335eaa5117&query=${input}`)
            .then(response => response.data.results)
            .then(movie => { setMovies(movie) })
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div class="input-group input-group-sm mb-3">
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="inputGroup-sizing-sm">ðŸ”Ž</span>
                    </div>
                    <input onChange={(e) => { setInput(e.target.value) }} value={input} type="text" class="form-control" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-sm" placeholder="choose a movie" />
                </div>
            </form>
            <MovieSearch movies={movies}/>
        </div>
    );
};

export default Home;
